import { useEffect, useState } from "react"
import BookForm from "./BookForm"
import BooksTable from "./BooksTable"
import { Box} from '@mui/material'
import Axios from "axios"


const Books = ()=>{
    const [books,setBooks] = useState([]);
    const [submitted, setSubmitted] = useState(false);
    const [isEdit, setIsEdit] = useState(false);

    useEffect(()=>{
        getBooks(); 
    },[]);
    const getBooks = () =>{
        Axios.get('http://localhost:3001/api/books')
            .then(response =>{
                setBooks(response.data?.response||[]);
            })
            .catch(error =>{
                console.error("Axios Error: ",error)
            })
    }

    const addBook = (data) =>{
        setSubmitted(true);
        const payload ={
            id: data.id,
            name: data.name,
            author: data.author
        }
        Axios.post('http://localhost:3001/api/addbook',payload)
            .then(()=>{
                getBooks();
                setSubmitted(false);
                isEdit(false);
            })
            .catch(error =>{
                console.error("Axios Error: ",error)
            })
    }
    const updateBook = (data) =>{
        setSubmitted(true);
        const payload ={
            id: data.id,
            name: data.name,
            author: data.author
        }
        Axios.post('http://localhost:3001/api/updatebook',payload)
            .then(()=>{
                getBooks();
                setSubmitted(false);
                isEdit(false);
            })
            .catch(error =>{
                console.error("Axios Error: ",error)
            })
    
    }

    return(
        <Box 
            sx={{
                padding: '100px',
                margin: 'auto',
                marginTop: '100px'
            }}
        >
            <BookForm
                addBook={addBook}
                submitted={submitted}
            />
            <BooksTable rows={books}/>
        </Box>
        
    )
}
export default Books;
